import React,{useState,useEffect} from 'react';

import Navbar from 'react-bootstrap/Navbar';
import Button from 'react-bootstrap/Button';
import Nav from 'react-bootstrap/Nav';
import Form from 'react-bootstrap/Form';
import FormControl from 'react-bootstrap/FormControl';

//service
import AuthService from '../service/auth.service';

import './navbar.scss';

const MyNavBar = (props) => {

    const user = props.user;
    const [isUser,setIsUser] = useState();


    const logOut = () => {
        AuthService.logout();
    };
    
    useEffect(() => { 
        if(user) {
            setIsUser(true)
        }
    }, [user]);

    return (
        <Navbar expand="lg" >
            <Navbar.Brand href={"/"}>Report Player</Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
                <Form inline className='ml-auto pr-5'>
                    <FormControl type="text" placeholder="Search" className="mr-sm-2" />
                    <Button variant="outline-success">Search</Button>
                </Form>
                <Nav className="pr-1">
                    <Nav.Link href={"/report"}  >Report</Nav.Link>
                    {user ? (<a href="/login" className="nav-link" onClick={logOut}>
                            Logout
                        </a>)
                        : (<div><Nav.Link className="nav-link" href={"/login"}>Login</Nav.Link></div>
                        )}
                        {!user && <Nav.Link href={"/register"} className="float-right">Sign up</Nav.Link>}
                    
                </Nav>
            </Navbar.Collapse>
        </Navbar>
    );

}

export default MyNavBar;
