import axios from "axios";


const token = localStorage.getItem("userToken");
var userRegion = localStorage.getItem("userRegion")
const headers = {headers: {"Bearer": token }};

if(userRegion) {
    userRegion = userRegion.replace(/ /g,"_"); 

}


const API_URL_REPORT = "http://192.168.1.235:8080/api/v1/reports?region="+userRegion;


const fetchReportByPlayerUsername = (username) => {
    const url = "http://192.168.1.235:8080/api/v1/reports/players/"+username+'?region='+userRegion;
    return axios.get(url);

};

const fetchLastReportOfRegion = () => {
    const url = "http://192.168.1.235:8080/api/v1/reports/last?region="+userRegion;
    return axios.get(url);
};

const doReport = (nickname,reportType,description) => {
    return axios.post(API_URL_REPORT, {
        nickname,reportType,description},headers
        )
    .then((response)=>{
        return response;
    });
};
export default {
    fetchReportByPlayerUsername,
    doReport,
    fetchLastReportOfRegion,
};
