import {FETCH_REPORT_BY_PLAYER_ID} from './actionType'
import axios from 'axios'

const token = localStorage.getItem("userToken");
const userRegino = localStorage.getItem("userRegion")

export const fetchReportByPlayerId = (username) => dispatch => {
    const url = 'http://localhost:8080/api/v1/reports/players/'+username+'&region='+userRegion;
    axios.get(url,{headers: {"Bearer": token }})
        .then(res =>
            dispatch({
                type: FETCH_REPORT_BY_PLAYER_ID,
                payload: res.data
            })
        );   
};
