import {FETCH_TOP_PLAYERS,FETCH_LATEST_PLAYERS} from './actionType'
import axios from 'axios'

const token = localStorage.getItem("userToken");
const userRegion = localStorage.getItem("userRegion");
export const fetchTopPlayer = () => dispatch => {
    axios.get('http://localhost:8080/api/v1/players/top?region='+userRegion)
        .then(res =>
            dispatch({
                type: FETCH_TOP_PLAYERS,
                payload: res.data
            })
        );
};
export const fetchLatestPlayers = () => dispatch => {
    axios.get('http://localhost:8080/api/v1/players/latest?region='+userRegion)
        .then(res => 
            dispatch({
                type: FETCH_LATEST_PLAYERS,
                payload: res.data
            }),
        );
};
