import { Chart } from "react-google-charts";
import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import './PieChartReportsTypeOfPlayer.scss';
import {fetchReportsTypeByPlayerNickname} from '../../redux/reportType/actions';


const PieChartReportsTypeOfPlayer = (props) => {

    const dispatch = useDispatch();
    const reportsType = useSelector(state => state.reportType.reportsTypeOfPlayer)
    const username = props.username;

    useEffect( ()=> {
        dispatch(fetchReportsTypeByPlayerNickname())
    },[dispatch])

    console.log(reportsType)
    return (
        <div className="pie-chart">
        <Chart
            width={'700px'}
            height={'700px'}
            chartType="PieChart"
            loader={<div>Loading Chart</div>}
            data={
                [
                    ['Task', 'Hours per Day'],
                    ['Afk', 11],
                    ['Trolling', 2],
                    ['Verbal Abuse', 2],
                    ['Rage Quit', 2],
                  ]
            }
            options={{
                alignment: "center",
                // Just add this option
                pieHole: 0.7,
                backgroundColor: 'transparent',
                colors: ['#40ff00', '#ff0000', '#ffffff', '#FFA500', '#FFA500']
            }}

            rootProps={{ 'data-testid': '3' }}
        />
        </div>
    );
};

export default PieChartReportsTypeOfPlayer;
