import React, { useState, useRef, useEffect } from 'react';
import { useForm } from "react-hook-form";
import { Col, Container, Row } from 'react-bootstrap';
import AuthService from '../../service/auth.service';
import './UpdatePassword.scss';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';


const UpdatePassword = () => {

    const { register, errors, handleSubmit, watch } = useForm({});

    const passwordWatched = useRef({});

    const [oldPassword, setOldPassword] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [message, setMessage] = useState("");
    const [showModal, setShowModal] = useState(false)

    const handleCloseModal = () => setShowModal(false);
    const handleOpenModal = () => setShowModal(true);

    passwordWatched.current = watch("password", "")

    const passwordNotFound = <p>Password Not Found</p>

    const onChangeOldPassword = (e) => {
        setOldPassword(e.target.value);
    };

    const onChangPassword = (e) => {
        setPassword(e.target.value);
    };

    const onChangeConfirmPassword = (e) => {
        setConfirmPassword(e.target.value);
    };

    const onSubmit = e => {
        AuthService.updatePassword(oldPassword, password, confirmPassword).then(
            (response) => {
                response.status === 200 ? setShowModal() : setMessage(passwordNotFound)
            });
    };

    return (
        <Container fluid>
            <Row className="justify-content-md=center">
                <Col>
                    <h5>Update Password</h5>
                    <form onSubmit={e => e.preventDefault()}>
                        {message}
                        <input
                            name="oldPassword"
                            value={oldPassword}
                            onChange={onChangeOldPassword}
                            className="form-control mb-2"
                            type="text"
                            placeholder="actual password"
                            ref={register({
                                required: "You must type an actual  password"
                            })}
                        />
                        {errors.oldPassword && <p>{errors.oldPassword.message}</p>}
                        <input
                            name="password"
                            onChange={onChangPassword}
                            className="form-control mb-2"
                            type="text"
                            placeholder="new password"
                            ref={register({
                                required: "You must type a new  password",
                                minLength: {
                                    value: 8,
                                    message: "Password must have at least 8 characters"
                                }
                            })}
                        />
                        {errors.password && <p>{errors.password.message}</p>}
                        <input
                            name="confirmPassword"
                            value={confirmPassword}
                            onChange={onChangeConfirmPassword}
                            className="form-control mb-2"
                            type="text"
                            placeholder="confirm password"
                            ref={register({
                                validate: value =>
                                    value === passwordWatched.current || "The passwords do not match"
                            })}
                        />
                        {errors.confirmPassword && <p>{errors.confirmPassword.message}</p>}
                        <input type="submit" className="form-control " onClick={handleSubmit(onSubmit)} />

                    </form>
                </Col>
            </Row>
            <Modal show={showModal} onHide={handleCloseModal}>
                <Modal.Body>
                    I will not close if you click outside me. Don't even try to press
                    escape key.
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleCloseModal}>
                        Close
                    </Button>
                </Modal.Footer>
            </Modal>
        </Container>
    );

};


export default UpdatePassword
