import React, { useEffect, useState } from 'react';
import Card from 'react-bootstrap/Card';
//css
import './CardReportsOfPlayer.scss'
import ReactPaginate from 'react-paginate';
import { Container } from 'react-bootstrap';
import axios from 'axios'


const CardReportsOfPlayer = (props) => {


    const token = localStorage.getItem("userToken");


    const username = props.username;

    const [offset, setOffset] = useState(0);
    const [data, setData] = useState();
    const [perPage] = useState(4);
    const [pageCount, setPageCount] = useState(0)


    useEffect(() => {
        getData()
    }, [offset])



    const getData = async () => {
        const url = 'http://localhost:8080/api/v1/reports/players/' + username;
        const res = await axios.get(url, { headers: { "Bearer": token } })
        const data = res.data;
        const slice = data.slice(offset, offset + perPage)
        
        const postData = slice.map(pd => <div key={pd.id}>
            <p>{pd.description}</p>
            <img src={pd.thumbnailUrl} alt=""/>
        </div>)
        setData(postData)
        console.log(data.length)

        setPageCount(Math.ceil(data.length / perPage))
    }

    const handlePageClick = (e) => {
        const selectedPage = e.selected;
        setOffset(selectedPage + 1)
    };

    return (
        <div >
            {data}
            <ReactPaginate
                previousLabel={"prev"}
                nextLabel={"next"}
                breakLabel={"..."}
                breakClassName={"break-me"}
                pageCount={pageCount}
                marginPagesDisplayed={2}
                pageRangeDisplayed={5}
                onPageChange={handlePageClick}
                containerClassName={"pagination"}
                subContainerClassName={"pages pagination"}
                activeClassName={"active"} />
        </div>
    )
}
export default CardReportsOfPlayer
