import axios from "axios";

const API_URL = "http://localhost:8080/api/v1/authenticate";
const API_URL_SIGNUP = "http://localhost:8080/api/public/v1/registration";

//attivare l utente solo se clicca l email di conferma

const register = (username, email, password, confirmPassword, region) => {
    return axios.post(API_URL_SIGNUP, {
        username,
        email,
        password,
        confirmPassword,
        region,
    });
};

const login = (username, password) => {
    return axios
        .post(API_URL, {
            username,
            password,
        })
        .then((response) => {
            if (response.data.jwt) {
                console.log(response.data.jwt)
                localStorage.setItem("user", response.data.jwt);
            }

            return response.data;
        });
};

const verifyUserExists = (username) => {
    const url = "http://localhost:8080/api/v1/users/username/"+username;
    return axios.get(url).then(
        (response) => { 
            console.log(response.status)
            return response
        });
};

const verifyEmailExists = (email) => {
    const url = "";
    return axios.get(url).then(
        (response) => {
            return response
        });
};

const logout = () => {
    localStorage.removeItem("user");
};

const getCurrentUser = () => {
    return localStorage.getItem("user");
};

export default {
    register,
    login,
    logout,
    getCurrentUser,
    verifyUserExists,
    verifyEmailExists,
};
