//component
import Header from './header/Header';
import HomePage from './pages/homePage/HomePage';
import PlayerPage from './pages/playerPage/PlayerPage';
import Login from './pages/Login/Login';
import Register from './pages/Register/Register';
import Message from './pages/Message/Message';
import Reporter from './pages/Report/Reporter';
import PrivateRoute from './utility/PrivateRoute';
import LoggedRouter from './utility/LoggedRouter';
import React, { useState, useEffect, useReducer } from "react";
import { Route, Switch, BrowserRouter as Router } from 'react-router-dom';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';

//class
import './assets/scss/default.scss';
//service
import AuthService from './service/auth.service';
import UserProfile from './pages/userPage/UserProfile';
//assets



const App = (props) => {

    const userRegion = localStorage.getItem("userRegion");

    const [currentUser, setCurrentUser] = useState(undefined);
    const [showModal, setShowModal] = useState(false)


    const handleOpenModal = () => setShowModal(true)

    const handleCloseModal = (e) => {
        localStorage.setItem("userRegion",e.target.value)
        setShowModal(false)
    }

    useEffect(() => {
        if(!userRegion) {
            handleOpenModal();
        }
        const user = AuthService.getCurrentUser();
        if (user) {
            setCurrentUser(user);
        }
    }, [userRegion]);


    return (
        <div>
            {userRegion ? (
                <div className="App">
                    <Header user={currentUser} />
                    <div className="image">
                        <Switch>
                            <Route path='/' exact component={HomePage} />
                            <Route path='/player/:username' component={PlayerPage} />
                            <Route path='/profile' component={UserProfile} />
                            <LoggedRouter exact path="/login" component={Login} />
                            <Route exact path="/register" component={Register} />
                            <Route exact path="/message" component={Message} />
                            <PrivateRoute path='/report' component={Reporter} />
                        </Switch>
                    </div>

                </div>) : (<p></p>)
            }
        </div>
    )

}

export default App;
