import React, {useState} from 'react';
import { useForm } from "react-hook-form";
import { Col, Container, Row } from 'react-bootstrap';
import AuthService from '../../service/auth.service';

const UpdatePassword = () => {

    const {register, errors, handleSubmit, watch}  = useForm({});
    const [actualPassword,setActualPassword] = useState();
    const [newPassword,setNewPassword] = useState();
    const [confirmPassword,setConfirmPassword] = useState();

  

    const onChangeActualPassword = (e) => {
        setActualPassword(e.target.value);
    };

    const onChangNewPassword = (e) => {
        setNewPassword(e.target.value);
    };

    const onChangeConfirmPassword = (e) => {
        setConfirmPassword(e.target.value);
    };

    const onSubmit = () => {
        AuthService.updatePassword(actualPassword,newPassword,confirmPassword).then(
            (response) => {
                console.log(response)
            }
        );
        
    };

    return (
        <Container fluid>
            <Row className="justify-content-md=center">
                <Col>
                    <form onSubmit={e=>e.preventDefault()}>
                        <input 
                            name="actualPassword"
                            value={actualPassword}
                            onChange={onChangeActualPassword}
                            className="form-control mb-2"
                            type="password"
                            placeholder="actual password"
                            ref={register({
                                required: "You must type an actual  password"
                            })}
                        />
                        {errors.actualPassword && <p>{errors.actualPassword.message}</p>}
                        <input 
                            name="newPassword"
                            value={newPassword}
                            onChange={onChangNewPassword}
                            className="form-control mb-2"
                            type="password"
                            placeholder="new  password"
                            ref={register({
                                required: "You must type a new  password"
                            })}
                        />
                        {errors.newPassword && <p>{errors.newPassword.message}</p>}
                        <input 
                            name="confirmPassword"
                            value={confirmPassword}
                            onChange={onChangeConfirmPassword}
                            className="form-control mb-2"
                            type="password"
                            placeholder="confirm password"
                            ref={register({
                                required: "You must type a confirm password"
                            })}
                        />
                        {errors.confirmPassword && <p>{errors.confirmPassword.message}</p>}
                        <input type="submit" className="form-control " onClick={handleSubmit(onSubmit)} />

                    </form>
                </Col>
            </Row>
        </Container>
    );

};


export default UpdatePassword
