import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {fetchReportsTypeByPlayerNickname} from '../../redux/reportType/actions';
import {ListGroup}  from 'react-bootstrap';

const dispatch = useDispatch();
    const reportsType = useSelector(state => state.reportType.reportsTypeOfPlayer)
    const username = props.username;

    useEffect( ()=> {
        dispatch(fetchReportsTypeByPlayerNickname(username))
    },[dispatch,username])

    const data = [['Task', 'Hours per Day']]


    reportsType.map(repType => {
		return data.push([repType.reportType,repType.count])
		
	});

const ListReportsTypeOfRegion = () => {
    return (
        < div className='list-latest-players' >
            <ListGroup>
                {reportType.map((repType, index) =>
                    <div key={index} className='mb-2 list'>
                            <ListGroup.Item key='index'>
                                {repType.reportType}
                            </ListGroup.Item>
                    </div>
                )}
            </ListGroup>
        </div >
    );
};

export default ListReportsTypeOfRegion;
