import {FETCH_REPORTS_TYPE_BY_PLAYER_NICKNAME,FETCH_ALL_REPORTS_TYPE_BY_REGION} from './actionType';
import axios from 'axios';
import { useReducer } from 'react';


const token = localStorage.getItem("userToken");
const userRegion = localStorage.getItem("userRegion");


export const fetchReportsTypeByPlayerNickname = (nickname) => dispatch => {
    const url = 'http://localhost:8080/api/public/v1/players/reportsType?username='+nickname+'?region='+userRe;
    axios.get(url,{headers: {"Bearer": token }})
        .then(res =>
            dispatch({
                type: FETCH_REPORTS_TYPE_BY_PLAYER_NICKNAME,
                payload: res.data
            })
        );   
};

export const fetchAllReportsType = (region) => dispatch => {
    
    const url = "http://localhost:3001/api/public/v1/reportsType/"+region;
    axios.get(url)
        .then(res =>
            dispatch({
                type: FETCH_ALL_REPORTS_TYPE_BY_REGION,
                payload: res.data
            })
        );   
};
