import React, {useEffect} from 'react'
import {useDispatch, useSelector} from 'react-redux'
import {fetchReportsTypeByPlayerNickname} from '../redux/reportType/actions';
import {ListGroup}  from 'react-bootstrap';

//css
import './ListReportTypeOfPlayer.scss';

const ListReportTypeOfPlayer = (props) => {

    const dispatch = useDispatch();
    const reportsType = useSelector(state => state.reportType.reportsTypeOfPlayer)
    const username = props.username;
    
    useEffect(() => {
        dispatch(fetchReportsTypeByPlayerNickname(username))
    },[dispatch,username])

    console.log(reportsType)
    return (
        <div className='report-type'>
            <ListGroup>
            {reportsType.map((reportType,index) =>
                <ListGroup.Item key={index} className='mb-2'>
                   <p> {reportType.reportType}</p>
                </ListGroup.Item>
            )}
            </ListGroup>
        </div>
    );
};

export default ListReportTypeOfPlayer;
