import axios from 'axios';

const userRegion = localStorage.getItem('userRegion');

const searchPlayer = (username) => {
    const url = 'http://localhost:8080/api/v1/players/search?username='+username+'&region='+userRegion;
    return axios.get(url).then(
        (response) => {
            return response
        } 
    );
};

export default {
    searchPlayer,
};
