import {FETCH_COMMENT_OF_PLAYER} from './actionsType'
import axios from 'axios';


export const fetchCommentOfPlayer = (id) => dispatch => {
    const url = 'http://localhost:3001/api/public/v1/comments/'+id;
    axios.get(url)
        .then(res => 
            dispatch({
                type: FETCH_COMMENT_OF_PLAYER,
                payload: res.data
            })
        );
};
