import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import { Col, Container, Row } from 'react-bootstrap';
import AuthService from '../../service/auth.service';
import reportsType from '../../utility/reportsTypeConstant';

const Reporter = (props) => {

    const { register, errors, handleSubmit } = useForm({});

    const [nickname, setNickname] = useState("");
    const [reportType, setReportType] = useState("");
    const [description, setDescription] = useState("");
   
    
    const onChangeNickname = (e) => {
        setNickname(e.target.value);
    };

    const onChangeReportType = (e) => {
        setReportType(e.target.value);
    };

    const onChangeDescription = (e) => {
        setDescription(e.target.value);
    };



    const onSubmit = e => {
        AuthService.report(nickname,reportType,description).then(
            (response) => {
                if(response.status===200) {
                    props.history.push("/player/"+nickname);
                }
                else {
                    console.log("error")
                }
            }
        );
       
    };


    return (
        <Container flui>
            <Row className="justify-content-md-center">
                <Col className="col-lg-4 form-login">
                    <form onSubmit={e => e.preventDefault()}>
                        <input
                            name="nickname"
                            value={nickname}
                            onChange={onChangeNickname}
                            placeholder="nickname"
                            className="form-control mb-2"
                            type="text"
                            ref={register({
                                required: "You must specify an nickname"
                            })}
                        />
                        {errors.nickname && <p>{errors.nickname.message}</p>}

                        <select name="reportType"
                            value={reportType}
                            onChange={onChangeReportType}
                            className="form-control mb-2"
                            ref={register({
                                required: "select one option"
                            })}>
                            <option value="">Select a Report Type</option>
                            {reportTypes.map((repType, index) =>
                                <option value={repType}>{repType}</option>
                            )}
                        </select>

                        {errors.reportType && <p>{errors.reportType.message}</p>}

                        <textarea
                            name="description"
                            value={description}
                            onChange={onChangeDescription}
                            className="form-control mb-2"
                            ref={register({
                                required: "write an description's report"
                            })}
                        />
                        {errors.description && <p>{errors.description.message}</p>}
                        s
                        <input type="submit" className="form-control " onClick={handleSubmit(onSubmit)} />
                    </form>
                </Col>
            </Row>
        </Container>
    );
};

export default Reporter;
