import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import { Col, Container, Row } from 'react-bootstrap';
import ReportService from '../../service/report.service';
import {reportsType} from '../../utility/reportsTypeConstant';

const Reporter = (props) => {

    const { register, errors, handleSubmit } = useForm({});

    const [nickname, setNickname] = useState("");
    const [reportType, setReportType] = useState("");
    const [description, setDescription] = useState("");
   
    
   


    const onSubmit = data => {
        ReportService.doReport(data.nickname,data.reportType,description).then(
            (response) => {
                if(response.status===200) {
                    props.history.push("/player/"+nickname);
                }
                else {
                    console.log("error")
                }
            }
        );
       
    };


    return (
        <Container flui>
            <Row className="justify-content-md-center">
                <Col className="col-lg-4 form-login">
                    <form onSubmit={handleSubmit(onSubmit)}>
                        <input
                            name="nickname"
                            value={nickname}
                            onChange={onChangeNickname}
                            placeholder="nickname"
                            className="form-control mb-2"
                            type="text"
                            ref={register({
                                required: "You must specify an nickname"
                            })}
                        />
                        {errors.nickname && <p>{errors.nickname.message}</p>}

                        <select name="reportType"
                            value={reportType}
                            onChange={onChangeReportType}
                            className="form-control mb-2"
                            ref={register({
                                required: "select one option"
                            })}>
                            <option value="">Select a Report Type</option>
                            {reportsType.map((repType, index) =>
                                <option value={repType}>{repType}</option>
                            )}
                        </select>

                        {errors.reportType && <p>{errors.reportType.message}</p>}

                        <textarea
                            name="description"
                            value={description}
                            onChange={onChangeDescription}
                            className="form-control mb-2"
                            ref={register({
                                required: "write an description's report",
                                minLength: {
                                    value: 10,
                                    message: "min 8 letter"
                                },
                                maxLength: {
                                    value: 2000,
                                    message: "max 2000"
                                }
                            })}
                        />
                        {errors.description && <p>{errors.description.message}</p>}
                        
                        <input type="submit" className="form-control " />
                    </form>
                </Col>
            </Row>
        </Container>
    );
};

export default Reporter;
