import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Card from 'react-bootstrap/Card';
import './CardReportsOfPlayer.scss'
import ReactPaginate from 'react-paginate';
import { Container } from 'react-bootstrap';
import { fetchReportByPlayerId } from '../redux/report/actions';
import axios from 'axios'
import ReportService from '../service/report.service';

const CardReportsOfPlayer = (props) => {


    
    const token = localStorage.getItem("userToken");

    const username = props.username;

    const [offset, setOffset] = useState(0);
    const [perPage] = useState(4);
    const [pageCount, setPageCount] = useState(0)
    const [slice,setSlice] = useState("")

    const dispatch = useDispatch();
    const reports = useSelector(state => state.reports.reportsOfPlayer);


    const handlePageClick = (e) => {
        setOffset(e.selected)
    };

    const getData = async() => {
        const res = await ReportService.fetchReportByPlayerUsername(username)
        setSlice(offset,offset+res.data)
    };

     useEffect(() => {
        getData()
    }, [ offset])

    return (
        <Container >
            {reports.map((report, index) => <Card key={index}>
            <Card.Body>
                <Card.Title>
                    <Card.Link href="#"></Card.Link>
                </Card.Title>
                <div className='info-rep '>
                    <div className='d-inline report-type mr-1'>{report.id}</div>
                    <div className='d-inline report-type mr-1'>{report.reportType} in Ranked</div>
                    <div className='d-inline float-right date'>2/8/2020 </div>
                    <div className='d-inline report-by '>Reported By </div>
                    <div className='d-inline user'><Card.Link className='mr-1' href="#">Rolad</Card.Link></div>
                </div>
                <div className='line mb-3'>
                </div>
                <div className='report-text'>
                    <p>{report.description}</p>
                </div>
                <div className='br mb-3'>
                </div>
            </Card.Body>
        </Card>)}
            <ReactPaginate
                previousLabel={"prev"}
                nextLabel={"next"}
                breakLabel={"..."}
                breakClassName={"break-me"}
                pageCount={pageCount}
                marginPagesDisplayed={2}
                pageRangeDisplayed={5}
                onPageChange={handlePageClick}
                containerClassName={"pagination"}
                subContainerClassName={"pages pagination"}
                activeClassName={"active"} />
        </Container>
    )
}
export default CardReportsOfPlayer
