import React, { useState, useRef } from "react";
import Select from "react-validation/build/select";
import { useForm } from "react-hook-form";


import { Col, Container, Row } from 'react-bootstrap';

//service
import AuthService from '../../service/auth.service';

//scss
import './Register.scss';

const vregion = (value) => {
    if (!value) {
        return (
            <div className="alert alert-danger" role="alert">
                Please Select the region
            </div>
        );
    }
};




const Register = (props) => {

    const { register, errors, handleSubmit, watch } = useForm({});

    const passwordWatched = useRef({});

    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [region, setRegion] = useState("");
    const [email, setEmail] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [message, setMessage] = useState("");
    const [successful, setSuccessful] = useState(false)

    passwordWatched.current = watch("password", "")


    const onChangeUsername = (e) => {
        setUsername(e.target.value)
    };

    const onChangePassword = (e) => {
        setPassword(e.target.value)
    };

    const onChangeEmail = (e) => {
        setEmail(e.target.value)
    };

    const onChangeConfirmPassword = (e) => {
        setConfirmPassword(e.target.value)
    };

    const onChangeRegion = (e) => {
        setRegion(e.target.value)
    };


    const onSubmit = async data => {

        
        AuthService.register(username, email, password, confirmPassword, region).then(
            (response) => {
                setMessage("A verification link has been sent your email: " + email)
                setSuccessful(true)
                props.history.push("/message/${message}");
            },
            (error) => {
                const resMessage =
                    (error.response &&
                        error.response.data &&
                        error.response.data.message) ||
                    error.message ||
                    error.toString();
                setMessage(resMessage)
                setSuccessful(false)
                props.history.push({
                    pathname : '/message',
                    state :{
                    message : message
                    }
                    } 
                  );
            }
        );
    };


    const regions = ["euw", "na", "korea", "japan"];


    return (
        <Container fluid>
            <Row>
                <Col className='col-lg-4'>
                </Col>
                <Col className="col-lg-4 form-login">
                    <form onSubmit={e => e.preventDefault()}>
                        <input
                            name="username"
                            value={username}
                            onChange={onChangeUsername}
                            className="form-control mb-2 "
                            type="text"
                            placeholder="username"
                            ref={register({
                                required: "You must specify a username",
                                minLength: {
                                    value: 8,
                                    message: "username must have at least 6 characters"
                                }
                            })}
                        />

                        {errors.username && <p>{errors.username.message}</p>}

                        <input
                            name="email"
                            value={email}
                            onChange={onChangeEmail}
                            className="form-control mb-2 "
                            type="text"
                            placeholder="email"
                            ref={register({
                                required: "required",
                                pattern: {
                                    value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                                    message: "invalid email address"
                                }
                            })}
                        />

                        {errors.email && <p>{errors.email.message}</p>}

                        <input
                            name="password"
                            onChange={onChangePassword}
                            className="form-control mb-2 "
                            type="text"
                            placeholder="password"
                            ref={register({
                                required: "You must specify a password",
                                minLength: {
                                    value: 8,
                                    message: "Password must have at least 8 characters"
                                }
                            })}
                        />
                        {errors.password && <p>{errors.password.message}</p>}

                        <input
                            name="confirmPassword"
                            value={confirmPassword}
                            onChange={onChangeConfirmPassword}
                            className="form-control mb-2"
                            placeholder="confirm password"
                            type="text"
                            ref={register({
                                validate: value =>
                                    value === passwordWatched.current || "The passwords do not match"
                            })}
                        />

                        {errors.confirmPassword && <p>{errors.confirmPassword.message}</p>}

                        <select name="region"
                            value={region}
                            onChange={onChangeRegion}
                            className="form-control mb-2"
                            ref={register({
                                required: "select one option"
                            })}>
                            <option value="">Select a region</option>
                            {regions.map((region, index) =>
                                <option value={region}>{region}</option>
                            )}
                        </select>

                        {errors.region && <p>{errors.region.message}</p>}

                        <input type="submit" className="form-control " onClick={handleSubmit(onSubmit)} />
                    </form>
                </Col>
                <Col lassName='col-lg-4'>
                </Col>
            </Row>
        </Container>
    )

};

export default Register;
