import React, { useState, useEffect } from 'react';

import Navbar from 'react-bootstrap/Navbar';
import Button from 'react-bootstrap/Button';
import Nav from 'react-bootstrap/Nav';
import Form from 'react-bootstrap/Form';
import FormControl from 'react-bootstrap/FormControl';
import Modal from 'react-bootstrap/Modal';
//service
import AuthService from '../service/auth.service';

import './navbar.scss';

const MyNavBar = (props) => {


    const user = props.user;
    const [isUser, setIsUser] = useState(false);
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);


    const logOut = () => {
        AuthService.logout();
    };

    useEffect(() => {
        if (user) {
            setIsUser(true)
        }
    }, [user]);


    return (

        <Navbar expand="lg" >
            <Navbar.Brand href={"/"}>Report Player</Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
                <Form inline className='ml-auto pr-5'>
                    <FormControl type="text" placeholder="Search" className="mr-sm-2" />
                    <Button variant="outline-success">Search</Button>
                </Form>
                <Nav className="pl-2">
                    <Nav.Link className="mr-2" href={"/report"}> Report </Nav.Link>
                    {isUser ? (<div className="row">
                        <Button variant="primary" onClick={handleShow}>
                            Launch demo modal
                        </Button><a href="/login" className="nav-link" onClick={logOut}> Logout</a>
                        <a href="/login" className="nav-link" onClick={logOut}>{ }</a></div>
                    )
                        : (<div><Nav.Link className="nav-link" href={"/login"}>Login</Nav.Link></div>
                        )}
                    {!user && <Nav.Link href={"/register"} className="float-right">Sign up</Nav.Link>}
                </Nav>
            </Navbar.Collapse>
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Modal heading</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    Woohoo, you're reading this text in a modal!
                            </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                                </Button>
                    <Button variant="primary" onClick={handleClose}>
                        Save Changes
                    </Button>
                </Modal.Footer>
            </Modal>
        </Navbar>

    );

}

export default MyNavBar;
