import {FETCH_REPORTS_TYPE_BY_PLAYER_ID,FETCH_ALL_REPORTS_TYPE_BY_REGION} from './actionType'


const initialState = {
    reportsTypeOfPlayer: [{
    }],
    reportsTypeOfRegion: [{

    }]
};

const reportTypeReducers = (state = initialState,action) => {
    switch(action.type) {
        case FETCH_REPORTS_TYPE_BY_PLAYER_ID:
            return {
                ...state,
                reportsTypeOfPlayer: action.payload
            }
        case FETCH_ALL_REPORTS_TYPE_BY_REGION:
            return {
                ...state,
                reportsTypeOfRegion: action.payload
            }
        default:
            return state;
    }
}


export default reportTypeReducers;
