import axios from "axios";

const API_URL_SIGNIN = "http://192.168.1.235:8080/api/v1/authentication/";
const API_URL_SIGNUP = "http://192.168.1.235:8080/api/v1/registration/";
const API_URL_REPORT = "http://localhost:8080/api/v1/reports";
const API_URL_UPDATE_PASSWORD = "http://localhost:8080/api/v1/users/1/account/update_password";


const token = localStorage.getItem("userToken");
const headers = { headers: { "Bearer": token } };
const region = localStorage.getItem("userRegion")


const forgotPassword = (password,confirmPassword,token) => {
    const url = "http://192.168.1.235:8080/api/v1/accounts/update_forgot_password";
    return axios.post(url,{password,confirmPassword,token}).then(
        (response) => {
            return response.status;
        })
        .catch((error)=>{
            return error.response.status;
        });
};

const updatePassword = (oldPassword, password, confirmPassword) => {
    return axios.post("http://192.168.1.235:8080/api/v1/users/accounts/update_password", {
        oldPassword, password, confirmPassword
    }, headers)
        .then((response) => {
            return response.status
        })
};

const sendEmailForgotPassword = (email) => {
    const url = "http://192.168.1.235:8080/api/v1/accounts/email/email_token";
    return axios.post(url, { email }).then(
        (response) => {
            return response.status
        })
        .catch(
            (error) => {
                console.log(error)
            });
};

const register = (username, email, password, confirmPassword) => {
    return axios.post(API_URL_SIGNUP, {
        username,
        email,
        password,
        confirmPassword,
        region,
    });
};



const login = (username, password) => {
    return axios
        .post(API_URL_SIGNIN, {
            username,
            password,
        })
        .then((response) => {

            if (response.data) {
                localStorage.setItem("userToken", response.data);
                return response;
            }

        });
};

const verifyUserExists = (username) => {
    const url = "http://192.168.1.235:8080/api/v1/users/username/" + username;
    return axios.get(url).then(
        (response) => {
            return response
        })
        .catch(error => {
        });
};

const verifyEmailExists = (email) => {
    const url = "http://192.168.1.235:8080/api/v1/users/" + email;
    return axios.get(url).then(
        (response) => {
            return response
        });
};

const logout = () => {
    localStorage.removeItem("userToken");
};

const getCurrentUser = () => {
    return localStorage.getItem("userToken");
};

export default {
    register,
    login,
    logout,
    getCurrentUser,
    verifyUserExists,
    verifyEmailExists,
    updatePassword,
    sendEmailForgotPassword,
    forgotPassword,
};
