import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Card } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { fetchTopPlayer } from '../../redux/player/actions'

//css
import '../css/CardTopPlayers.scss';


const CardTopPlayers = (props) => {

    const dispatch = useDispatch();
    const players = useSelector(state => state.players.topPlayers);

    useEffect(() => {
        dispatch(fetchTopPlayer())
    }, [dispatch]);

    return (
        players.map((player, index) =>
            <div key={index} className="card-top-players">
                <Card >
                    <Card.Body>
                        <Card.Title className="title-top-player">
                            <div className='d-inline'><Link to ={"player/" + player.id}>{player.nickname}</Link></div> 
                            <div className='d-inline rep-count ml-2'>{player.reportCount}</div>
                            <div className='d-inline reported'>reported</div>
                        </Card.Title>
                        <div className="text-top-player"><p>last reported: {player.data}</p></div>
                    </Card.Body>
                </Card>
                <br/>
            </div>
        )
    );
};

export default CardTopPlayers;
