import React, { useState, useRef, useEffect } from 'react';
import { useForm } from "react-hook-form";
import { Col, Container, Row } from 'react-bootstrap';
import AuthService from '../../service/auth.service';
import './UpdatePassword.scss';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';


const UpdatePassword = () => {

    const { register, errors, handleSubmit, watch } = useForm({});

    const passwordWatched = useRef({});

    const [oldPassword, setOldPassword] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [message, setMessage] = useState("");
    const [showModal, setShowModal] = useState(false)

    passwordWatched.current = watch("password", "")
    const passwordNotFound = <p>Password Not Found</p>


    const handleCloseModal = () => {
        setShowModal(false);
        window.location.reload();
    }
    const handleOpenModal = () => setShowModal(true);


  

    const onSubmit = data => {
        AuthService.updatePassword(data.oldPassword, data.password, data.confirmPassword).then(
            (response) => {
                response.status === 200 ? handleOpenModal() : setMessage(passwordNotFound)
            });
    };

    return (
        <div>
            <h5>Update Password</h5>
            <form onSubmit={handleSubmit(onSubmit)}>
                {message}
                <input
                    name="oldPassword"
                    className="form-control mb-2"
                    type="text"
                    placeholder="actual password"
                    ref={register({
                        required: "You must type an actual  password"
                    })}
                />
                {errors.oldPassword && <p>{errors.oldPassword.message}</p>}
                <input
                    name="password"
                    className="form-control mb-2"
                    type="text"
                    placeholder="new password"
                    ref={register({
                        required: "You must type a new  password",
                        minLength: {
                            value: 8,
                            message: "Password must have at least 8 characters"
                        }
                    })}
                />
                {errors.password && <p>{errors.password.message}</p>}
                <input
                    name="confirmPassword"
                    className="form-control mb-2"
                    type="text"
                    placeholder="confirm password"
                    ref={register({
                        validate: value =>
                            value === passwordWatched.current || "The passwords do not match"
                    })}
                />
                {errors.confirmPassword && <p>{errors.confirmPassword.message}</p>}
                <input type="submit" className="form-control " />

            </form>

            <Modal show={showModal} onHide={handleCloseModal}>
                <Modal.Body>
                    Password Updated
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleCloseModal}>
                        Close
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );

};


export default UpdatePassword
