import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import './FormEmailForgotPassword.scss';
import { useHistory } from 'react-router';
import AuthService from '../../service/auth.service';



const FormEmailForgotPassword = (props) => {

    let history = useHistory()
    const { register, errors, handleSubmit } = useForm({});
    const [successful, setSuccessful] = useState()


    const onSubmit = data => {
        AuthService.sendEmailForgotPassword(data.email).then(
            (response) => {
                response === 200 ? setSuccessful(true) : setSuccessful(false)
            },
            (error) => {
                console.log(error)
            }

        );
    };

    return (
        <div>
            {successful}
            <form onSubmit={handleSubmit(onSubmit)}>
                <input
                    type="text"
                    placeholder="email"
                    name="email"
                    className="form-control mb-2"
                    ref={register({
                        required: "required"
                    })}
                />
                <input
                    type="submit"
                    className="form-control"
                />
                {errors.email && <p>{errors.email.message}</p>}
            </form>
        </div>


    );

};

export default FormEmailForgotPassword;
