import {FETCH_COMMENT_OF_PLAYER} from './actionsType';


const initialState = {
    commentsOfPlayer: [{
        //id: '1',
        //nickname: 'foo'
    }]
};


const commentReducers = (state = initialState,action) => {
    switch(action.type) {
        case FETCH_COMMENT_OF_PLAYER:
            return {
                ...state,
                commentsOfPlayer: action.payload
            }
        default:
            return state;
    }
}

export default commentReducers
