import React, { useState, useRef } from "react";
import Select from "react-validation/build/select";
import { useForm } from "react-hook-form";


import { Col, Container, Row } from 'react-bootstrap';

//service
import AuthService from '../../service/auth.service';

//scss
import './Register.scss';

const vregion = (value) => {
    if (!value) {
        return (
            <div className="alert alert-danger" role="alert">
                Please Select the region
            </div>
        );
    }
};




const Register = (props) => {

    const { register, errors, handleSubmit, watch } = useForm({});

    const password = useRef({});

    password.current = watch("password", "");

    const onSubmit = async data => {
        alert(data.region);
    };


    const regions = ["euw", "na", "korea", "japan"];


    return (
        <Container fluid>
            <Row>
                <Col className='col-lg-4'>
                </Col>
                <Col className="col-lg-4 form-login">
                    <form onSubmit={e => e.preventDefault()}>
                        <input
                            name="username"
                            className="form-control mb-2 "
                            type="text"
                            placeholder="username"
                            ref={register({
                                required: "You must specify a username",
                                minLength: {
                                    value: 4,
                                    message: "username must have at least 6 characters"
                                }
                            })}
                        />

                        {errors.username && <p>{errors.username.message}</p>}

                        <input
                            name="email"
                            className="form-control mb-2 "
                            type="text"
                            placeholder="email"
                            ref={register({
                                required: "required",
                                pattern: {
                                    value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                                    message: "invalid email address"
                                  }
                            })}
                        />

                        {errors.email && <p>{errors.email.message}</p>}

                        <input
                            name="password"
                            className="form-control mb-2 "
                            type="password"
                            placeholder="password"
                            ref={register({
                                required: "You must specify a password",
                                minLength: {
                                    value: 8,
                                    message: "Password must have at least 8 characters"
                                }
                            })}
                        />
                        {errors.password && <p>{errors.password.message}</p>}

                        <input
                            name="password_repeat"
                            className="form-control mb-2"
                            placeholder="confirm password"
                            type="password"
                            ref={register({
                                validate: value =>
                                    value === password.current || "The passwords do not match"
                            })}
                        />

                        {errors.password_repeat && <p>{errors.password_repeat.message}</p>}

                        <select name="region"
                            className="form-control mb-2"
                            ref={register({
                                required: "select one option"
                            })}>
                            <option value="">Select a region</option>
                            {regions.map((region,index) =>
                                <option value={index}>{region}</option>
                            )}
                        </select>

                        {errors.region && <p>{errors.region.message}</p>}

                        <input type="submit" className="form-control " onClick={handleSubmit(onSubmit)} />
                    </form>
                </Col>
                <Col lassName='col-lg-4'>
                </Col>
            </Row>
        </Container>
    )

};

export default Register;
