import { Chart } from "react-google-charts";
import React, {useEffect} from 'react';
import {useDispatch,useSelector} from 'react-redux';
import {fetchAllReportsType} from '../redux/reportType/actions';


const PieChartAllReports = props =>  {

	const disptach = useDispatch();
	const reportsType = useSelector(state => state.reportType.reportsTypeOfRegion)
	const region = props.region;

	useEffect(() => {
		disptach(fetchAllReportsType(region));
	},[disptach,region]);

	const data = [['Task', 'Hours per Day']]

	reportsType.map(repType => {
		return data.push([repType.reportType,repType.count])
		
	});

	return (
		<Chart
			width={'500px'}
			height={'500px'}
			chartType="PieChart"
			loader={<div>Loading Chart</div>}
			data={
				data
			}
    		options={{
				alignment: "center",
				pieStartAngle: 100,
				// Just add this option
				pieHole: 0.8,
				backgroundColor: 'transparent',
				colors: ['#40ff00', '#ff0000', '#ffffff', '#FFA500', '#FFA500']
			}}
			
    		rootProps={{ 'data-testid': '3' }}
		/>
   );
};

export default PieChartAllReports;
