import React, { useState, useEffect } from 'react';

import Navbar from 'react-bootstrap/Navbar';
import Button from 'react-bootstrap/Button';
import Nav from 'react-bootstrap/Nav';
import Form from 'react-bootstrap/Form';
import Modal from 'react-bootstrap/Modal';
//service
import AuthService from '../service/auth.service';

import './navbar.scss';
import playerService from '../service/player.service';

const MyNavBar = (props) => {

    const userRegion = localStorage.getItem("userRegion")

    const user = props.user;
    const [isUser, setIsUser] = useState(false);
    const [show, setShow] = useState(false);
    const [searchPlayer,setSearchPlayer] = useState("")

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);


    const logOut = () => {
        AuthService.logout();
    };

     const handleRemoveRegion = () =>  {
        localStorage.removeItem("userRegion")
        window.location.reload()
    }

    const onChangeSearchPlayer = (e) => {
        setSearchPlayer(e.target.value)
    };
    
    const onClickSearchPlayer = () => {
        if(searchPlayer)
        props.history.push("/player/"+searchPlayer);
    };
 

    useEffect(() => {
        if (user) {
            setIsUser(true)
        }
    }, [user]);


    return (

        <Navbar expand="lg" >
            <Navbar.Brand href={"/"}>Report Player</Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
                <Button variant="outline-success" onClick={handleRemoveRegion}>{userRegion}</Button>
                <Form inline className='ml-auto pr-5'>
                    <input type="text" onChange={onChangeSearchPlayer} placeholder="Search" value={searchPlayer} className="mr-sm-2 form-control" />
                    <Button variant="outline-success" onClick={onClickSearchPlayer} >Search</Button>
                </Form>
                <Nav className="pl-2">
                    <Nav.Link className="mr-2" href={"/report"}> Report </Nav.Link>
                    {isUser ? (<div className="row">
                        <Nav.Link className="nav-link" onClick={handleShow}>Settings</Nav.Link>
                    </div>)
                        : (<div><Nav.Link className="nav-link" href={"/login"}>Login</Nav.Link></div>
                        )}
                    {!user && <Nav.Link href={"/register"} className="float-right">Sign up</Nav.Link>}
                </Nav>
            </Navbar.Collapse>
            <Modal show={show} onHide={handleClose}>
                <Modal.Footer>
                    <a href="/login" className="nav-link" onClick={logOut}> Logout</a>
                    <a href="/profile" className="nav-link" > Settings</a>
                </Modal.Footer>
            </Modal>
        </Navbar>

    );

}

export default MyNavBar;
