import React, { useState, useRef } from "react";
import Form from "react-validation/build/form";
import Input from "react-validation/build/input";
import CheckButton from "react-validation/build/button";
import Select from "react-validation/build/select";

import { isEmail } from "validator";
import { Col, Container, Row } from 'react-bootstrap';

//service
import AuthService from '../../service/auth.service';

const required = (value) => {
    if (!value) {
        return (
            <div className="alert alert-danger" role="alert">
                This is not a valid email.
            </div>
        );
    }
};

const validEmail = (value) => {
    if (!isEmail(value)) {
        return (
            <div className="alert alert-danger" role="alert">
                This is not a valid email.
            </div>
        );
    }
};

const vusername = (value) => {
    if (value.length < 3 || value.length > 16) {
        return (
            <div className="alert alert-danger" role="alert">
                The username must be between 3 and 16 characters.
            </div>
        );
    }
};

const vregion = (value) => {
    if(!value) {
        return (
            <div className="alert alert-danger" role="alert">
                Please Select the region
        </div>
        );
    }
};

const vpassword = (value) => {
    if (value.length < 6 || value.length > 40) {
        return (
            <div className="alert alert-danger" role="alert">
                The password must be between 6 and 40 characters.
            </div>
        );
    }
}


const Register = (props) => {

    const form = useRef();
    const checkBtn = useRef();

    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [successful, setSuccessful] = useState(false);
    const [message, setMessage] = useState("");

    const regions = ["euw","na","korea","japan"];

    const onChangeUsername = (e) => {
        const username = e.target.value;
        setUsername(username)
    };

    const onChangeEmail = (e) => {
        const email = e.target.value;
        setEmail(email)
    };

    const onChangePassword = (e) => {
        const password = e.target.value;
        setPassword(password)
    };

    const handlerRegister = (e) => {
        e.preventDefault();

        setMessage("");
        setSuccessful(false);

        form.current.validateAll();

        if (checkBtn.current.context._errors.length === 0) {
            AuthService.register(username, email, password).then(
                (response) => {
                    setMessage(response.data.message);
                    setSuccessful(true)
                },
                (error) => {
                    const resMessage = (error.response && error.response.data && error.response.message) ||
                        error.message || error.toString();

                    setMessage(resMessage);
                    setSuccessful(false);
                }
            );
        }
    };

    return (
        <Container fluid>
            <Row>
                <Col className='col-lg-4'>
                </Col>
                <Col className="col-lg-4 form-login">
                    <Form onSubmit={handlerRegister} ref={form}>
                        {!successful && (
                            <div>
                                <Input
                                    type="text"
                                    className="form-control mb-2"
                                    name="username"
                                    placeholder="username"
                                    value={username}
                                    onChange={onChangeUsername}
                                    validations={[required, vusername]}
                                />
                                <Input
                                    type="text"
                                    className="form-control mb-2"
                                    name="email"
                                    placeholder="email"
                                    value={email}
                                    onChange={onChangeEmail}
                                    validations={[required, validEmail]}
                                />

                                <Input
                                    type="password"
                                    className="form-control mb-2"
                                    name="password"
                                    placeholder="password"
                                    value={password}
                                    onChange={onChangePassword}
                                    validations={[required, vpassword]}
                                />
                                <Select name='Region' className="form-control mb-2" value='' validations={[vregion]}>
                                    <option value=''>Select Region</option>
                                    {regions.map((region,index) => <option value={index}>{region}</option> )}
                                </Select>

                                <button className="btn btn-primary btn-block">Sign Up</button>
                            </div>
                        )}
                        {message && (
                            <div
                                className={successful ? "alert alert-success" : "alert alert-danger"}
                                role="alert"
                            >
                                {message}
                            </div>
                        )}
                        <CheckButton style={{ display: "none" }} ref={checkBtn} />
                    </Form>
                </Col>
                <Col lassName='col-lg-4'>
                </Col>
            </Row>
        </Container>
    )

};

export default Register;
