import axios from "axios";


const token = localStorage.getItem("userToken");
const userRegion = localStorage.getItem("userRegion")

const fetchReportByPlayerUsername = (username) => {
    const url = "http://localhost:8080/api/v1/reports/players/"+username+'?region='+userRegion;
    return axios.get(url);

};

const doReport = (nickname,reportType,description) => {
    return axios.post(API_URL_REPORT, {
        nickname,reportType,description})
    .then((response)=>{
        return response;
    });
};

export default {
    fetchReportByPlayerUsername,
    doReport,
};
