import React, { useState, useEffect } from 'react';
import { useHistory } from "react-router"

import Navbar from 'react-bootstrap/Navbar';
import Button from 'react-bootstrap/Button';
import Nav from 'react-bootstrap/Nav';
import Form from 'react-bootstrap/Form';
import Modal from 'react-bootstrap/Modal';
import Dropdown from 'react-bootstrap/Dropdown';
import { regions } from '../utility/serverRegionConstant';
import ImageSettings from '../assets/img/settings.png';

//service
import AuthService from '../service/auth.service';

import './navbar.scss';

const MyNavBar = (props) => {

    let history = useHistory()

    const userRegion = localStorage.getItem("userRegion")

    const user = props.user;
    const [isUser, setIsUser] = useState(false);
    const [show, setShow] = useState(false);
    const [searchPlayer, setSearchPlayer] = useState("")
    const [showRegionModal, setShowRegionModal] = useState(false)

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);


    const logOut = () => {
        AuthService.logout();
    };


    const onClickOpenRegionModal = () => {
        setShowRegionModal(true)
    }


    const onHideRegionModal = () => {
        setShowRegionModal(false)
        window.location.reload();
    }
    const onClickRegionModal = e => {
        localStorage.setItem("userRegion", e.target.value)
        setShowRegionModal(false)
        window.location.reload();
    }

    const onChangeSearchPlayer = (e) => {
        setSearchPlayer(e.target.value)
    };

    const onClickSearchPlayer = () => {
        const url = encodeURI('/player/' + searchPlayer)
        history.push(url);
    };

    const imgSetting = <img src={ImageSettings} width='42px' height='42px'></img>
    const CustomToggle = React.forwardRef(({ children, onClick }, ref) => (
        <a
          href=""
          ref={ref}
          onClick={e => {
            e.preventDefault();
            onClick(e);
          }}
        >
          {imgSetting}
          {children}
        </a>
      ));

    useEffect(() => {
        if (user) {
            setIsUser(true)
        }
    }, [user]);


    return (
        <Navbar expand="lg" >
            <Navbar.Brand href={"/"}>League of  Report</Navbar.Brand>
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
                <Button variant="outline-success" onClick={onClickOpenRegionModal} >{userRegion}</Button>
                <Form inline className='ml-auto pr-5' onSubmit={e => e.preventDefault()}>
                    <input type="text" onChange={onChangeSearchPlayer} placeholder="Search" value={searchPlayer} className="mr-sm-2 form-control" />
                    <Button variant="outline-success" onClick={onClickSearchPlayer} >Search</Button>
                </Form>
                <Nav className="pl-2">
                    <Nav.Link className="mr-3 mt-2" href={"/report"}> Report </Nav.Link>
                    {isUser ? (<div className="row">
                        <Dropdown>
                            <Dropdown.Toggle as={CustomToggle} id="dropdown-custom-components" drop="left">
                                
                             </Dropdown.Toggle>
                            <Dropdown.Menu>
                                <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
                                <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
                                <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
                            </Dropdown.Menu>
                        </Dropdown>
                    </div>)
                        : (<div><Nav.Link className="nav-link" href={"/login"}>Login</Nav.Link></div>
                        )}
                    {!user && <Nav.Link href={"/register"} className="float-right">Sign up</Nav.Link>}
                </Nav>
            </Navbar.Collapse>
            <Modal show={showRegionModal} onHide={onHideRegionModal} >
                <Modal.Body>
                    <div>
                        {regions.map((region) =>
                            <Button variant="primary" size="sm" value={region} block onClick={onClickRegionModal}>
                                {region}
                            </Button>
                        )}
                        <br />
                    </div>
                </Modal.Body>
            </Modal>
        </Navbar>
    );

}

export default MyNavBar;
