import React, {useState,useRef} from 'react';
import { useForm } from "react-hook-form";
import { Col, Container, Row } from 'react-bootstrap';
import AuthService from '../../service/auth.service';

const UpdatePassword = () => {

    const {register, errors, handleSubmit, watch}  = useForm({});

    const passwordWatched = useRef({});

    const [oldPassword,setOldPassword] = useState();
    const [password,setPassword] = useState();
    const [confirmPassword,setConfirmPassword] = useState();

    passwordWatched.current = watch("password", "")

  

    const onChangeOldPassword = (e) => {
        setOldPassword(e.target.value);
    };

    const onChangPassword = (e) => {
        setPassword(e.target.value);
    };

    const onChangeConfirmPassword = (e) => {
        setConfirmPassword(e.target.value);
    };

    const onSubmit = e => {
        AuthService.updatePassword(password,password,confirmPassword).then(
            (response) => {
                console.log(response)
            }
        );
        
    };

    return (
        <Container fluid>
            <Row className="justify-content-md=center">
                <Col>
                    <form onSubmit={e => e.preventDefault()}>
                        <input 
                            name="actualPassword"
                            value={oldPassword}
                            onChange={onChangeOldPassword}
                            className="form-control mb-2"
                            type="text"
                            placeholder="actual password"
                            ref={register({
                                required: "You must type an actual  password"
                            })}
                        />
                        {errors.actualPassword && <p>{errors.actualPassword.message}</p>}
                        <input 
                            name="newPassword"
                            value={password}
                            onChange={onChangPassword}
                            className="form-control mb-2"
                            type="text"
                            placeholder="new password"
                            ref={register({
                                required: "You must type a new  password",
                                minLength: {
                                    value: 8,
                                    message: "Password must have at least 8 characters"
                                }
                            })}
                        />
                        {errors.newPassword && <p>{errors.newPassword.message}</p>}
                        <input 
                            name="confirmPassword"
                            value={confirmPassword}
                            onChange={onChangeConfirmPassword}
                            className="form-control mb-2"
                            type="text"
                            placeholder="confirm password"
                            ref={register({
                                validate: value =>
                                    value === passwordWatched.current || "The passwords do not match"
                            })}
                        />
                        {errors.confirmPassword && <p>{errors.confirmPassword.message}</p>}
                        <input type="submit" className="form-control " onClick={handleSubmit(onSubmit)} />

                    </form>
                </Col>
            </Row>
        </Container>
    );

};


export default UpdatePassword
