import React, { useState, useRef, useEffect } from "react";
import { useForm } from "react-hook-form";
import { Col, Container, Row ,Button} from 'react-bootstrap';
//service
import AuthService from '../../service/auth.service';

//scss
import './Register.scss';


const Register = (props) => {

    const userRegion = localStorage.getItem("userRegion")

    const { register, errors, handleSubmit, watch } = useForm({});

    const passwordWatched = useRef({});


    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [region, setRegion] = useState("");
    const [email, setEmail] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [usernameAlreadyExist, setUsernameAlreadyExist] = useState();
    const [emailAlreadyExists, setEmailAlreadyExists] = useState();

    passwordWatched.current = watch("password", "")

    useEffect(() => {
        setRegion(userRegion)
    }, [usernameAlreadyExist])

    const onChangeUsername = (e) => {
        setUsername(e.target.value)
    };

    const onChangePassword = (e) => {
        setPassword(e.target.value)
    };

    const onChangeEmail = (e) => {
        setEmail(e.target.value)
    };

    const onChangeConfirmPassword = (e) => {
        setConfirmPassword(e.target.value)
    };


    const onSubmit = e => {

        AuthService.verifyUserExists(username).then(
            (response) => {
                response.status === 200 ? setUsernameAlreadyExist(true) : setUsernameAlreadyExist(false)
            }
        );

        AuthService.verifyEmailExists(email).then(
            (response) => {
                response.status === 200 ? setEmailAlreadyExists(true) : setEmailAlreadyExists(false)
            }
        );


    };
    if (usernameAlreadyExist === false && emailAlreadyExists === false) {
        AuthService.register(username, email, password, confirmPassword, region).then(
            (response) => {
                props.history.push({
                    pathname: '/message',
                    state: {
                        successful: true,
                        message: "A verification link has been sent your email: " + email
                    }
                });
            },
            (error) => {
                const resMessage =
                    (error.response &&
                        error.response.data &&
                        error.response.data.message) ||
                    error.message ||
                    error.toString();

                props.history.push({
                    pathname: '/message',
                    state: {
                        successful: false,
                        message: resMessage
                    }
                });
            }
        );
    }



    return (
        <Container fluid>
            <Row className="justify-content-md-center">
                <Col className="col-lg-4 form-login">
                    <form onSubmit={e => e.preventDefault()}>
                        <input
                            name="username"
                            value={username}
                            onChange={onChangeUsername}
                            className="form-control mb-2 "
                            type="text"
                            placeholder="username"
                            ref={register({
                                required: "You must specify a username",
                                minLength: {
                                    value: 8,
                                    message: "username must have at least 6 characters"
                                }
                            })}
                        />
                        {errors.username && <p>{errors.username.message}</p>}
                        {usernameAlreadyExist && <p>Username Already Exists</p>}

                        <input
                            name="email"
                            value={email}
                            onChange={onChangeEmail}
                            className="form-control mb-2 "
                            type="text"
                            placeholder="email"
                            ref={register({
                                required: "required",
                                pattern: {
                                    value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                                    message: "invalid email address"
                                }
                            })}
                        />

                        {errors.email && <p>{errors.email.message}</p>}
                        {emailAlreadyExists && <p>email Already exists</p>}

                        <input
                            name="password"
                            onChange={onChangePassword}
                            className="form-control mb-2 "
                            type="text"
                            placeholder="password"
                            ref={register({
                                required: "You must specify a password",
                                minLength: {
                                    value: 8,
                                    message: "Password must have at least 8 characters"
                                }
                            })}
                        />
                        {errors.password && <p>{errors.password.message}</p>}

                        <input
                            name="confirmPassword"
                            value={confirmPassword}
                            onChange={onChangeConfirmPassword}
                            className="form-control mb-2"
                            placeholder="confirm password"
                            type="text"
                            ref={register({
                                validate: value =>
                                    value === passwordWatched.current || "The passwords do not match"
                            })}
                        />

                        {errors.confirmPassword && <p>{errors.confirmPassword.message}</p>}
                        <Button className="form-control ">
                            {region}
                        </Button>
                        <input type="submit" className="form-control " onClick={handleSubmit(onSubmit)} />
                    </form>
                </Col>
            </Row>
        </Container>
    )

};

export default Register;
