import axios from "axios";

const API_URL_SIGNIN = "http://192.168.1.235:8080/api/v1/public/authentication";
const API_URL_SIGNUP = "http://192.168.1.235:8080/api/v1/public/registration";
const API_URL_REPORT = "http://localhost:8080/api/v1/reports";

//attivare l utente solo se clicca l email di conferma

const token = localStorage.getItem("userToken");


const updatePassword = (actualPassword,newPassword,confirmPassword) => {
    
}
const register = (username, email, password, confirmPassword, region) => {
    return axios.post(API_URL_SIGNUP, {
        username,
        email,
        password,
        confirmPassword,
        region,
    });
};

const report = (nickname,reportType,description) => {
    return axios.post(API_URL_REPORT, {
        nickname,reportType,description},{headers: {"Bearer": token }
    })
    .then((response)=>{
        return response;
    });
};

const login = (username, password) => {
    return axios
        .post(API_URL_SIGNIN, {
            username,
            password,
        })
        .then((response) => {
            
            if (response.data) {
                localStorage.setItem("userToken", response.data[0]);
                localStorage.setItem("userRegion", response.data[1]);
                return response;
            }
            
        });
};

const verifyUserExists = (username) => {
    const url = "http://192.168.1.235:8080/api/v1/users/username/"+username;
    return axios.get(url).then(
        (response) => { 
            return response
        })
        .catch(error => {
        });
};

const verifyEmailExists = (email) => {
    const url = "http://192.168.1.235:8080/api/v1/users/"+email;
    return axios.get(url).then(
        (response) => {
            return response
        });
};

const logout = () => {
    localStorage.removeItem("userToken");
};

const getCurrentUser = () => {
    return localStorage.getItem("userToken");
};

export default {
    register,
    login,
    logout,
    getCurrentUser,
    verifyUserExists,
    verifyEmailExists,
    report,
};
