import axios from "axios";

const API_URL = "http://localhost:8080/api/v1/authenticate";
const API_URL_SIGNUP = "http://localhost:8080/api/public/v1/registration";

//attivare l utente solo se clicca l email di conferma
const register = (username, email, password,region) => {
    return axios.post(API_URL, {
        username,
        email,
        password,
        region,
    });
};

const login = (username, password) => {
    return axios
        .post(API_URL , {
            username,
            password,
        })
        .then((response) => {
            if (response.data.jwt) {
                console.log(response.data.jwt)
                localStorage.setItem("user",response.data.jwt);
            }

            return response.data;
        });
};

const logout = () => {
    localStorage.removeItem("user");
};

const getCurrentUser = () => {
    return localStorage.getItem("user");
};

export default {
    register,
    login,
    logout,
    getCurrentUser,
};
