import {FETCH_REPORTS_TYPE_BY_PLAYER_NICKNAME,FETCH_ALL_REPORTS_TYPE_BY_REGION} from './actionType';
import axios from 'axios';


const token = localStorage.getItem("userToken");
const region = localStorage.get


export const fetchReportsTypeByPlayerNickname = (nickname) => dispatch => {
    const url = 'http://localhost:8080/api/public/v1/players/reportsType?username='+nickname;
    axios.get(url,{headers: {"Bearer": token }})
        .then(res =>
            dispatch({
                type: FETCH_REPORTS_TYPE_BY_PLAYER_NICKNAME,
                payload: res.data
            })
        );   
};

export const fetchAllReportsTypeByRegion = (region) => dispatch => {
    const url = 'http://localhost:3001/api/public/v1/reportsType/regions/'+region;
    axios.get(url)
        .then(res =>
            dispatch({
                type: FETCH_ALL_REPORTS_TYPE_BY_REGION,
                payload: res.data
            })
        );   
};
